package Entity;

public class HotelConsumer {
	Integer id;

	Integer consumerno;
	float food;
	float dianhua;
	float fruit;
	public HotelConsumer() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Integer getId() {
		return id;
	}
	public void setId(Integer id) {
		this.id = id;
	}
	public Integer getConsumerno() {
		return consumerno;
	}
	public void setConsumerno(Integer consumerno) {
		this.consumerno = consumerno;
	}
	public float getFood() {
		return food;
	}
	public void setFood(float food) {
		this.food = food;
	}
	public float getDianhua() {
		return dianhua;
	}
	public void setDianhua(float dianhua) {
		this.dianhua = dianhua;
	}
	public float getFruit() {
		return fruit;
	}
	public void setFruit(float fruit) {
		this.fruit = fruit;
	}
	
}
